mapper.select:
SELECT COUNT(1)
FROM base_device bd



            bd.device_code = #{deviceDO.deviceCode}


            AND bd.device_mn = #{deviceDO.deviceMn}


            AND bd.device_name LIKE CONCAT('%',CONCAT(#{deviceDO.deviceName},'%'))


            AND bd.field_id = #{deviceDO.fieldId}


            AND bd.institution_id = #{deviceDO.institutionId}

SELECT device_id, device_code, device_mn, device_name, field_id, manufacturer_id, institution_id, device_status, device_ip, device_port, device_pwd, map_x, map_y, gps_x, gps_y, supervise_organization, build_unit, device_manager, device_address, monitor_area, make_minute, make_hour, make_day, reply_flag, reply_force, make_flow, pipe_area, hour_count, gas_path, switch_time, system_version, operate_user, operate_time FROM base_device bd
bd.device_code = #{deviceDO.deviceCode}
AND bd.device_mn = #{deviceDO.deviceMn}
AND bd.device_name LIKE CONCAT('%',CONCAT(#{deviceDO.deviceName},'%'))
AND bd.field_id = #{deviceDO.fieldId}
AND bd.institution_id = #{deviceDO.institutionId}
LIMIT #{deviceDO.rowIndex},#{deviceDO.rowCount}

mapper.update:
UPDATE base_device SET device_code = #{deviceDO.deviceCode}, device_mn = #{deviceDO.deviceMn}, device_name = #{deviceDO.deviceName}, field_id = #{deviceDO.fieldId}, manufacturer_id = #{deviceDO.manufacturerId}, institution_id = #{deviceDO.institutionId}, device_status = #{deviceDO.deviceStatus}, device_ip = #{deviceDO.deviceIp}, device_port = #{deviceDO.devicePort}, device_pwd = #{deviceDO.devicePwd}, map_x = #{deviceDO.mapX}, map_y = #{deviceDO.mapY}, supervise_organization = #{deviceDO.superviseOrganization}, build_unit = #{deviceDO.buildUnit}, device_manager = #{deviceDO.deviceManager}, device_address = #{deviceDO.deviceAddress}, monitor_area = #{deviceDO.monitorArea}, make_minute = #{deviceDO.makeMinute}, make_hour = #{deviceDO.makeHour}, make_day = #{deviceDO.makeDay}, reply_flag = #{deviceDO.replyFlag}, reply_force = #{deviceDO.replyForce}, make_flow = #{deviceDO.makeFlow}, pipe_area = #{deviceDO.pipeArea}, hour_count = #{deviceDO.hourCount}, system_version = #{deviceDO.systemVersion}, operate_user = #{deviceDO.operateUser}, operate_time = #{deviceDO.operateTime}
AND device_id = #{deviceDO.deviceId}
AND device_id IS NULL

