mapper.select:
SELECT COUNT(1)
FROM `system_user` su



            su.user_id = #{userDO.userId}


            AND su.user_code = #{userDO.userCode}


            AND su.user_password = #{userDO.userPassword}


            AND su.user_stop = #{userDO.userStop}


            AND su.user_name LIKE CONCAT('%',CONCAT(#{userDO.userName},'%'))

SELECT user_id, user_code, user_name, user_password, user_phone, user_email, organization_id, user_stop, login_time, operate_user, operate_time FROM `system_user` su
su.user_id = #{userDO.userId}
AND su.user_code = #{userDO.userCode}
AND su.user_password = #{userDO.userPassword}
AND su.user_stop = #{userDO.userStop}
AND su.user_name LIKE CONCAT('%',CONCAT(#{userDO.userName},'%'))
LIMIT #{userDO.rowIndex},#{userDO.rowCount}

mapper.update:
UPDATE `system_user` SET user_code = #{userDO.userCode}, user_name = #{userDO.userName}, user_password = #{userDO.userPassword}, user_phone = #{userDO.userPhone}, user_email = #{userDO.userEmail}, organization_id = #{userDO.organizationId}, user_stop = #{userDO.userStop}, login_time = #{userDO.loginTime}, operate_user = #{userDO.operateUser}, operate_time = #{userDO.operateTime}
AND user_id = #{userDO.userId}
AND user_id IS NULL

